const { MendixPlatformClient } = require('mendixplatformsdk');

async function main() {
    // Connect to Mendix Platform
    const client = new MendixPlatformClient();

    // Get your app by ID
    const app = client.getApp('YOUR-APP-ID-HERE');

    // Create a temporary working copy from main branch
    const workingCopy = await app.createTemporaryWorkingCopy('main');
    console.log('Working copy created:', workingCopy.id);

    // Open the model for reading/writing
    const model = await workingCopy.openModel();

    // List all modules
    const modules = model.allModules();
    console.log('Modules:', modules.map(m => m.name));

    // List all domain models
    const domainModels = model.allDomainModels();
    console.log('Domain models:', domainModels.length);

    // IMPORTANT: Close without committing for read-only operations
    // Or commit changes:
    // await model.flushChanges();
    // await workingCopy.commitToRepository('main', { commitMessage: 'SDK changes' });
}

main().catch(console.error);

