using Mendix.StudioPro.ExtensionsAPI;
using Mendix.StudioPro.ExtensionsAPI.UI.Menu;

[Extension]
public class MyExtension : ExtensionBase
{
    public override void Initialize(IExtensionHost host)
    {
        host.MenuManager.AddMenuItem(
            MenuLocations.MainMenu.Tools,
            new MenuItemConfiguration
            {
                Caption = "My Custom Tool",
                OnClick = () => host.DialogService.ShowMessage("Hello from extension!")
            });
    }
}

