function Invoke-MxDump {
    param(
        [string]$MprPath,
        [string[]]$Modules,
        [string]$UnitType
    )
    $mxPath = "D:\Program Files\Mendix\11.5.0\modeler\mx.exe"
    $args = @('dump-mpr')
    if ($Modules) { $args += "--module-names=$($Modules -join ',')" }
    if ($UnitType) { $args += "--unit-type=$UnitType" }
    $args += "`"$MprPath`""
    & $mxPath $args
}
