.feedback--footer {
    margin-top: 3rem;
    padding: 2rem;
    border-top-width: 0.15rem;
    border-top-style: solid;
    border-top-color: #146FF4;
    width: 100%;
  }
  .feedback-container {
    margin-right: 2rem;
  }
  .feedback--answer {
    display: inline-block;
  }
  .feedback--answer-no {
    margin-left: 1em;
  }
  .feedback--response {
    display: none;
    margin-top: 1em;
  }
  .feedback--response__visible {
    display: block;
  }
    .feedback--footer,
    .get-started-container {
      display: flex;
      flex-wrap: wrap;
      justify-content: space-between;
    }
    .get-started-container {
      width:fit-content;
    }
    .get-started svg {
      max-width: 100%;
      max-height: 100%;
    }

