const { MendixPlatformClient } = require('mendixplatformsdk');

async function deployToAcceptance(appId, branchName = 'main') {
  const client = new MendixPlatformClient({
    mendixToken: process.env.MENDIX_TOKEN
  });

  try {
    // Get app
    const app = client.platform().app(appId);

    // Create deployment package
    console.log('Creating deployment package...');
    const version = `v${Date.now()}`;
    const buildJob = await app.createDeploymentPackage(branchName, -1, version);

    // Wait for build (polling)
    let buildStatus = 'running';
    while (buildStatus === 'running') {
      await new Promise(resolve => setTimeout(resolve, 10000)); // Wait 10s
      const job = await app.getBuildJob(buildJob.id);
      buildStatus = job.status;
      console.log(`Build status: ${buildStatus}`);
    }

    if (buildStatus !== 'success') {
      throw new Error(`Build failed with status: ${buildStatus}`);
    }

    // Get acceptance environment
    const environments = await app.getEnvironments();
    const acceptance = environments.find(env => env.name === 'Acceptance');

    if (!acceptance) {
      throw new Error('Acceptance environment not found');
    }

    // Deploy
    console.log('Deploying to Acceptance...');
    await acceptance.deploy(buildJob.packageId);

    // Start app
    console.log('Starting app...');
    await acceptance.start();

    console.log('Deployment complete!');
    return { version, packageId: buildJob.packageId };
  } catch (error) {
    console.error('Deployment failed:', error);
    throw error;
  }
}

// Usage
deployToAcceptance('app-id-here').then(console.log);

const { MendixPlatformClient } = require('mendixplatformsdk');
const { MendixModelSdk } = require('mendixmodelsdk');

async function provisionNewProject(projectName, templateAppId) {
  const client = new MendixPlatformClient({
    mendixToken: process.env.MENDIX_TOKEN
  });

  try {
    // Create new app
    console.log(`Creating app: ${projectName}`);
    const newApp = await client.platform().createNewApp(projectName, {
      summary: 'Provisioned from template',
      positioning: { x: 100, y: 100 }
    });

    // Get template
    const templateApp = client.platform().app(templateAppId);

    // Create working copy of template
    console.log('Loading template...');
    const templateWC = await client.platform().createOnlineWorkingCopy(
      templateApp,
      { revision: -1 }
    );

    // Create working copy of new app
    const newWC = await client.platform().createOnlineWorkingCopy(
      newApp,
      { revision: -1 }
    );

    try {
      // Open models
      const templateModel = await templateWC.openModel();
      const newModel = await newWC.openModel();

      // Copy domain models (simplified - real implementation more complex)
      const templateDMs = templateModel.allDomainModels();
      for (const dm of templateDMs) {
        await dm.load();
        // Copy entities, attributes, associations
        // (Actual implementation requires deep cloning)
      }

      // Commit changes
      console.log('Committing new project...');
      await newModel.commitToRepository('main', {
        commitMessage: 'Initial project from template'
      });

      console.log('Project provisioned successfully!');
      return { appId: newApp.appId, name: projectName };
    } finally {
      await templateWC.close();
      await newWC.close();
    }
  } catch (error) {
    console.error('Provisioning failed:', error);
    throw error;
  }
}

// Usage
provisionNewProject('CustomerPortal', 'template-app-id').then(console.log);

const { MendixPlatformClient } = require('mendixplatformsdk');
const fs = require('fs').promises;
const path = require('path');

async function backupAllProjects(backupDir) {
  const client = new MendixPlatformClient({
    mendixToken: process.env.MENDIX_TOKEN
  });

  try {
    // Get all apps
    const apps = await client.platform().getApps();
    console.log(`Found ${apps.length} apps to backup`);

    // Create backup directory
    await fs.mkdir(backupDir, { recursive: true });

    const results = [];

    for (const appInfo of apps) {
      try {
        console.log(`Backing up: ${appInfo.name}`);
        const app = client.platform().app(appInfo.appId);

        // Create offline working copy (.mpk)
        const workingCopy = await client.platform().createOfflineWorkingCopy(
          app,
          { revision: -1 }
        );

        // Download
        const mpkPath = path.join(backupDir, `${appInfo.name}_${Date.now()}.mpk`);
        await workingCopy.download(mpkPath);

        console.log(`  Saved to: ${mpkPath}`);
        results.push({ app: appInfo.name, path: mpkPath, status: 'success' });
      } catch (error) {
        console.error(`  Failed: ${error.message}`);
        results.push({ app: appInfo.name, status: 'failed', error: error.message });
      }
    }

    return results;
  } catch (error) {
    console.error('Backup process failed:', error);
    throw error;
  }
}

// Usage
backupAllProjects('./backups').then(console.log);

