entry_point_patterns.web_typescript:
import { ExtensionContext } from '@mendix/extensions-api';

export function activate(context: ExtensionContext) {
  context.subscriptions.push(
    context.commands.registerCommand('myExt.hello', () => {
      context.showInformationMessage('Hello from extension!');
    })
  );
}

entry_point_patterns.dotnet_csharp:
using Mendix.StudioPro.ExtensionsAPI;

public class MyExtension : IExtension
{
    public void Activate(IExtensionContext context)
    {
        var menu = context.GetService<IMenuService>();
        menu.AddMenuItem(new MenuItem { Title = "My Action" });
    }
    public void Deactivate() { }
}
